import { Stack } from '@mui/material';
import { categories } from '../utils/constants';

const Sidebar = ({selectedCategory,setSelectedCategory}) =>
(
    <Stack direction="row" sx={{ overflow: "auto", height: { sx: 'auto', md: '95%' }, flexDirection: { md: 'column' }, }}
    >

        {categories.map((category) => (
            <button
                className="category-btn"
                onClick={() => setSelectedCategory(category.name)}
                style={{
                    backround: category.name === selectedCategory && '#FC1503', color: 'white'
                }}

                key={category.name}
            >
                <span style={{ color: category.name === selectedCategory ? 'white' : 'blue', marginRight: '15px' }} >{category.icon}</span>



                <span style={{ opacity: category.name === selectedCategory ? '1' : '0.8' }}>{category.name}</span>
            </button>
        ))

        }
    </Stack>
)


export default Sidebar
